class Solution:
    def maximumPoints(self, edges: List[List[int]], coins: List[int], k: int) -> int:
        n = len(coins)
        g = defaultdict(list)
        for x, y in edges:
            g[x].append(y)
            g[y].append(x)

        @lru_cache(None)
        def dfs(node: int, parent: int) -> int:
            res = coins[node] - k
            for nei in g[node]:
                if nei == parent: continue
                res = max(res, dfs(nei, node) + coins[node] // 2)
            return res

        return max(dfs(i, -1) for i in range(n))
