class Solution:
    def maximizeSquareArea(self, m: int, n: int, hFences: List[int], vFences: List[int]) -> int:
        hFences = sorted([0] + hFences + [m])
        vFences = sorted([0] + vFences + [n])
        MOD = 10**9 + 7

        def f(a: List[int], mx: int) -> Set[int]:
            s = set()
            for i in range(len(a) - 1):
                if a[i + 1] - a[i] <= mx:
                    s.add(a[i] + a[i + 1] + mx)
            return s

        def calc(h, v):
            return max([min(hx, vx) for hx in h for vx in v] + [0])

        h = f(hFences, n)
        v = f(vFences, m)

        return max(calc(h, v), calc(v, h)) % MOD
