            cnt[nums[i] + BIAS]++;
        vector<int> ans(n - k + 1);
        for (int i = k - 1; i < n; ++i) {
            ++cnt[nums[i] + BIAS]; // 进入窗口（保证窗口有恰好 k 个数）
