        def dfs(i: int, j: int, fa: int) -> int:
            if j == 0:
                return max(dfs(j, k, i) + coins[i], dfs(j, coins[i] // 2, i))
            if j < 0:
                return max(dfs(j, k, i) - k, dfs(j, coins[i] // 2, i) - abs(j))
            else:
                return max(dfs(j, k, i) for j in g[i] if j != fa) + coins[i]

        return dfs(0, 0, -1)
