    private long countDigitOne(long num) {
        this.num = num;
        int m = 64 - Integer.numberOfLeadingZeros((int) num);
        memo = new long[m][m + 1];
        for (long[] row : memo) {
            Arrays.fill(row, -1);
        }
        return dfs(m - 1, 0, true);
