    # op=1，添加一个 size
    # op=-1，移除一个 size
    def update(self, size: int, op: int) -> None:
        i = len(self.t) - size
        while i < len(self.t):
            self.t[i][0] += op
            self.t[i][1] += op * size
            i += i & -i

    # 返回 >= size 的元素个数，元素和
    def query(self, size: int) -> (int, int):
        cnt = s = 0
