public int[] countOfPairs(int n, int x, int y) {
    int w[][] = new int[n + 1][n + 1];
    int INF = Integer.MAX_VALUE / 2;
    // Initialize the adjacency matrix.
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= n; j++)
            w[i][j] = (i == j ? 0 : INF);
    // The weight of the additional shortcut is 1. But it cannot be itself to itself, as the weight of itself to itself is 0.
    if (x != y) {
        w[x][y] = 1;
        w[y][x] = 1;
    }
    // Build the graph, adjacent front and back weights are both 1.
    for (int i = 1; i < n; i++) {
        w[i][i + 1] = 1;
        w[i + 1][i] = 1;
    }
    // Floyd's algorithm
    for (int p = 1; p <= n; p++)
        for (int st = 1; st <= n; st++)
            for (int end = 1; end <= n; end++)
                w[st][end] = Math.min(w[st][end], w[st][p] + w[p][end]);
    // First use the result array starting from 0 to calculate the result.
    int[] res = new int[n + 1];
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= n; j++)
            if (w[i][j] != INF)
                res[w[i][j]]++;
    // Finally, traverse the result array again to place the calculated result in the result array starting from 1.
    int[] result  = new int[n];
    for (int i = 0; i < n; i++)
        result[i] = res[i + 1];
    return result;
}

