def smallestBeautifulString(s, k):
    s = list(s)
    for i in range(len(s) - 1, -1, -1):
        blockedCharacters = set(s[max(0, i - 2):i])
        for j in range(1, 3):
            if i - j >= 0:
                blockedCharacters.add(s[i - j])
        for j in range(1, 4):
            if ord(s[i]) - ord('a') + j + 1 <= k and chr(ord('a') + j) not in blockedCharacters:
                s[i] = chr(ord(s[i]) + j)
                break
    return ''.join(s)
