public class Solution {
    public int[] countOfPairs(int n, int x, int y) {
        int w[][] = new int[n + 1][n + 1];
        int INF = Integer.MAX_VALUE / 2;
        //初始化 邻接矩阵。
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                w[i][j] = (i == j ? 0 : INF);
        //额外的捷径的权值是1。但不能是自身到自身，因为自身到自身的权值本来就是0.
        if (x != y) {
            w[x][y] = 1;
            w[y][x] = 1;
        }
        //建图，前后相邻 权值都是1。
        for (int i = 1; i < n; i++) {
            w[i][i + 1] = 1;
            w[i + 1][i] = 1;
        }
        //弗洛伊德算法
        for (int p = 1; p <= n; p++)
            for (int st = 1; st <= n; st++)
                for (int end = 1; end <= n; end++)
                    w[st][end] = Math.min(w[st][end], w[st][p] + w[p][end]);
        //先用下标从0开始的数组来求结果。
        int[] res = new int[n + 1];
        for (int i = 1; i <= n; i++)
            for (int j = 1; j <= n; j++)
                if (w[i][j] != INF)
                    res[w[i][j]]++;
        //最终再遍历一次把求得的结果放到 下标从1开始的result数组。
        int[] result  = new int[n];
        for (int i = 0; i < n; i++)
            result[i] = res[i + 1];
        return result;
    }
}

