    string generate(string s, int idx, int offset) {
        s[idx] += offset;
        for (int i = idx + 1; i < s.length(); ++i) {
            unordered_set<char> blockedCharacters;
            for (int j = 1; j <= 2; ++j) {
                if (i - j >= 0) {
                    blockedCharacters.insert(s[i - j]);
