public int minOrAfterOperations(int[] nums, int k) {
    int ans = 0;
    int mask = 0;
    for (int b = 29; b >= 0; b--) {
        mask |= 1 << b;
        int cnt = 0; 
        int and = -1;
        for (int x : nums) {
            and &= x & mask;
            if (and != 0) {
                cnt++;
            } else {
                and = -1;
            }
        }
        if (cnt > k) {
            ans |= 1 << b; 
            mask ^= 1 << b;
        }
    }
    return ans;
}

