        def update(i: int, op: int) -> None:
            idx = sl.bisect_left(i)
            pre = sl[idx - 1]
            nxt = sl[idx % len(sl)]

            t.update((nxt - pre - 1) % n + 1, -op)  # 移除/添加旧长度
            t.update((i - pre) % n, op)
            t.update((nxt - i) % n, op)  # 添加/移除新长度

        # 添加一个结束位置 i
        def add(i: int) -> None:
            if not sl:
                t.update(n, 1)
            else:
                update(i, 1)
            sl.add(i)

        # 移除一个结束位置 i
