class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if fa[x] != x:
                fa[x] = find(fa[x])
            return fa[x]

        res = []
        for i, (u, v) in enumerate(queries):
            if u > 0:
                fa[find(u - 1)] = v
            if v < n - 1:
                fa[find(v)] = find(u - 1)
