class BIT {
vector<int> tree;
public:
BIT(int n) : tree(n) {}

void inc(int i) {
    while (i < tree.size()) {
        ++tree[i];
        i += i & -i;
    }
}

int sum(int x) {
    int res = 0;
    while (x > 0) {
        res += tree[x];
        x &= x - 1;
    }
    return res;
}

int query(int left, int right) {
    return sum(right) - sum(left - 1);
}
};

