class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = defaultdict(list)
        vis = [0] * n

        def bfs(i: int) -> int:
            q = deque([0])
            for step in count(1):
