    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        replace_cost = {(x, y): z for x, y, z in zip(original, changed, cost)}
        N, M = len(source), len(target)

        @cache
        def dfs(i: int) -> int:
            if i == N:
                return 0
            min_cost = cost[i] if i < M else 0
            for j in range(i, N):
