class Graph {
public:
    using pii = pair<int, int>;
    Graph(int n, vector<vector<int>>& edges) {
        this->graph = vector<vector<pii>>(n);
        for (auto &vec : edges) {
            int x = vec[0];
            int y = vec[1];
            int cost = vec[2];
            graph[x].emplace_back(y, cost);
        }
    }
