from typing import List
from heapq import heappush, heappop

class Solution:
    def minimumVisitedCells(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        dist = [[-1]*n for _ in range(m)]
        dist[0][0] = 1
        row, col = [[] for _ in range(m)], [[] for _ in range(n)]
        def update(x, y):
            if x == -1 or y < x:
                return y
            else:
                return x
        for i in range(m):
            for j in range(n):
                while row[i] and row[i][0][1] + grid[i][row[i][0][1]] < j:
                    heappop(row[i])
                if row[i]:
                    dist[i][j] = update(dist[i][j], row[i][0][0]+1)
                while col[j] and col[j][0][1] + grid[col[j][0][1]][j] < i:
                    heappop(col[j])
                if col[j]:
                    dist[i][j] = update(dist[i][j], col[j][0][0]+1)
                if dist[i][j] != -1:
                    heappush(row[i], (dist[i][j], j))
                    heappush(col[j], (dist[i][j], i))
        return dist[m - 1][n - 1]
