class Solution {
    public boolean doesAliceWin(String s) {
        int[] dp = new int[s.length() + 1];
        int vowels = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            if (isVowel(s.charAt(i))) {
                vowels++;
            }
            dp[i] = (vowels % 2 == 0) ? dp[i + 1] : 1 - dp[i + 1];
