class Solution:
    def findChampion(self, n: int, edges: List[List[int]]) -> int:

        graph = [[] for _ in range(n)]
        for edge in edges:
            graph[edge[0]].append(edge[1])
        for i in range(n):

            visited = [False] * n
            count = self.dfs(graph, i, visited)

            if count == n:
                return i
        return -1

    def dfs(self, graph: List[List[int]], start: int, visited: List[bool]):
        count = 1
        visited[start] = True
        for node in graph[start]:
            if not visited[node]:
                count += self.dfs(graph, node, visited)
        return count

