from math import floor

class Solution:
    def maximumPoints(self, edges: List[List[int]], coins: List[int], k: int) -> int:
        g = [[] for _ in coins]
        for x, y in edges:
            g[x].append(y)
            g[y].append(x)

        @cache
        def dfs(i: int, j: int, fa: int) -> int:
            res = j if coins[i] > k else floor(coins[i] / 2)
            for x in g[i]:
                if x != fa:
                    res = max(res, dfs(x, j + max(0, coins[i] - k), i))
            return res

        return dfs(0, 0, -1)
