class FenwickTree:
    def __init__(self, n: int):
        self.tree = [0] * (n + 1)
        self.n = n

    @staticmethod
    def lowbit(x: int) -> int:
        return x & -x

    def update(self, x: int, delta: int) -> None:
        while x <= self.n:
            self.tree[x] += delta
            x += self.lowbit(x)

    def query(self, x: int) -> int:
        s = 0
        while x:
            s += self.tree[x]
            x -= self.lowbit(x)
        return s, s - self.tree[x]
