class Solution:
    def lastNonEmptyString(self, s: str) -> str:
        char_frequency = {}
        result = ""
        unique_chars = set()

        for c in s:
            char_frequency[c] = char_frequency.get(c, 0) + 1

        sorted_frequency = sorted(char_frequency.items(), key=lambda x: x[1], reverse=True)

        max_frequency = sorted_frequency[0][1]

        for p in sorted_frequency:
            if p[1] == max_frequency:
                unique_chars.add(p[0])
            if p[1] < max_frequency:
                break

        for i in range(len(s) - 1, -1, -1):
            if s[i] in unique_chars:
                result += s[i]
                unique_chars.remove(s[i])
            if not unique_chars:
                break

        return result[::-1]
