from typing import List

def resultGrid(image: List[List[int]], threshold: int) -> List[List[int]]:
    row, col = len(image), len(image[0])
    result = [[0 for _ in range(col)] for _ in range(row)]

    for i in range(row-2):
        for j in range(col-2):
            summ, count = 0, 0
            for x in range(i, i+3):
                for y in range(j, j+3):
                    if x+1 < i+3 and abs(image[x][y]-image[x+1][y]) > threshold:
                        break
                    if y+1 < j+3 and abs(image[x][y]-image[x][y+1]) > threshold:
                        break
                    summ += image[x][y]
                    count += 1
            if count == 9:
                average = summ // 9
                for x in range(i, i+3):
                    for y in range(j, j+3):
                        result[x][y] = average

    for i in range(row):
        for j in range(col):
            if result[i][j] == 0:
                result[i][j] = image[i][j]

    return result

