class Solution {
public:
    string lastNonEmptyString(string input_string) {
        unordered_map<char, int> char_frequency;
        string result = ""; 
        set<char> unique_chars;

        for (char c : input_string) {
            char_frequency[c]++;
        }

        vector<pair<char, int>> sorted_frequency(char_frequency.begin(), char_frequency.end());
        sort(sorted_frequency.begin(), sorted_frequency.end(), [](const pair<char, int>& a, const pair<char, int>& b) {
            return a.second > b.second;
        });

        int max_frequency = sorted_frequency[0].second;

        for (auto& p : sorted_frequency) {
            if (p.second == max_frequency) {
                unique_chars.insert(p.first);
            }
            if (p.second < max_frequency) {
                break;
            }
        }

        for (int i = input_string.length() - 1; i >= 0; --i) {
            if (unique_chars.find(input_string[i]) != unique_chars.end()) {
                result += input_string[i];
                unique_chars.erase(input_string[i]);
            }
            if (unique_chars.empty()) {
                break;
            }
        }

        reverse(result.begin(), result.end());
        return result;
    }
};
