class Solution {
    int n;
    vector<int> vec;
    vector<int> used;
public:
    int minSessions(vector<int>& tasks, int sessionTime) {
        ans = INT_MAX;
        n = tasks.size();
        used.resize(n);
        vector<int> vec = tasks;
        sort(vec.rbegin(), vec.rend());
        DFS(0, 0, 1, vec, sessionTime);
        return ans;
    }
    int ans;
    void DFS(int cur, int sum, int cnt, vector<int>& vec, int sessionTime) {
        if (sum + vec[cur] > sessionTime) {
            sum = 0;
            cnt++;
        }
        if (cnt + (n - cur) / (sessionTime - sum) >= ans) return;
        if (cur == n) {
            ans = cnt;
            return;
        }
        for (int i = cur; i < n; i++) {
            if (sum + vec[i] > sessionTime)
                return;
            if (!used[i]) {
                used[i] = 1;
                DFS(i + 1, sum + vec[i], cnt, vec, sessionTime);
                used[i] = 0;
            }
        }
    }
};



