#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
    bool prime[10000000];
    map<int,int>mp;
    void sieve() {
    int n=999999;

    memset(prime, true, sizeof(prime));
    for (int p = 2; p * p <= n; p++) {
        if (prime[p] == true) {
            for (int i = p * p; i <= n; i += p)
                prime[i] = false;
        }
    }
    }
    void solve(int i,int j,int n,int m,vector<vector<int>>& v,int s,vector<vector<bool>>&vis,int k) {
        if(i<0 || j<0 || i>=n || j>=m)
            return;
        s=10*s+v[i][j];
        if(s>10 && prime[s]) {
            mp[s]++;
        }
        solve(i+dx[k],j+dy[k],n,m,v,s,vis,k);
    }
    int mostFrequentPrime(vector<vector<int>>& v) {
        sieve();
        int n=v.size(),m=v[0].size();
        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++) {
                vector<vector<bool>>vis(n,vector<bool>(m,0));
                for(int k=0;k<8;k++) {
                    solve(i,j,n,m,v,0,vis,k);   
                }
            }
        }
        int mx=0;
        for(auto i:mp) {
            mx=max(mx,i.second);
        }
        if(mx==0)
            return -1;
        int ans=0;
        for(auto i:mp) {
            if(i.second==mx) {
                ans=max(ans,i.first);
            }
        }
        return ans;
    }
};

int main(){
    Solution s;
    vector<vector<int>>v={{1,2,3},{4,5,6},{7,8,9}};
    cout<<s.mostFrequentPrime(v);
    return 0;
}
