class Solution{
    public:
    Node* build_bridges(int parent[],int n,vector<int>&v)
    {
    Node* root=new Node(0);
    queue<Node*>q;
    q.push(root);
    for(int i=1;i<n;i++)
    {
        int x=parent[i];
        Node* node=q.front();
        q.pop();
        if(node->data==x)
        node->left=new Node(i);
        else
        node->right=new Node(i);

        q.push(node->left);
        q.push(node->right);
    }
    int j=0;
    int m=v.size();
    Node* r=root;
    while(root->right==NULL&&root->left==NULL)
    {
    if(v[j]==root->data)
    root=root->right;
    else
    root=root->left;
    j++;
    }
    return r;
    }

};
