import java.util.*;

class Solution {
  public int countMatchingSubarrays(int[] nums, int[] pattern) {
    int n = nums.length;
    int m = pattern.length;
    int count = 0;
    for (int i = 0; i <= n - m - 1; i++) {
      boolean validSubarray = true;
      for (int j = 0; j < m; j++) {
        if (pattern[j] == 1 && nums[i + j + 1] <= nums[i + j]) {
          validSubarray = false;
          break;
        } else if (pattern[j] == 0 && nums[i + j + 1] != nums[i + j]) {
          validSubarray = false;
          break;
        } else if (pattern[j] == -1 && nums[i + j + 1] >= nums[i + j]) {
          validSubarray = false;
          break;
        }
      }
      if (validSubarray) {
        count++;
      }
    }
    return count;
  }
}

public class Main {
  public static void main(String[] args) {

    Scanner sc = new Scanner(System.in);
    int n = sc.nextInt();
    int[] nums = new int[n];
    for (int i = 0; i < n; i++) {
      nums[i] = sc.nextInt();
    }

    int m = sc.nextInt();
    int[] pattern = new int[m];
    for (int i = 0; i < m; i++) {
      pattern[i] = sc.nextInt();
    }

    Solution sol = new Solution();
    int result = sol.countMatchingSubarrays(nums, pattern);
    System.out.println(result);
  }
}

