class Solution {
    public List<String> validStrings(int n) {
        List<String> ans = new ArrayList<>();
        for (int i = 0; i < 1<<n; i++) {
            String b = Integer.toBinaryString(i).substring(new String("0").repeat(n-Integer.toBinaryString(i).length()).length());
