class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        int n = s.size();
        for(int i = n - 1; i >= 0; i--) {
            unordered_set<char> blocked;
            for(int j = 1; j <= 2 && i - j >= 0; j++)
                blocked.insert(s[i-j]);
            for(int j = 1; j <= k; j++) {
                if(blocked.find(s[i] + j) == blocked.end()) {
                    return generate(s, i, j);
                }
            }
        }
        return "";
    }

    string generate(string s, int idx, int offset) {
        s[idx] += offset;
        for(int i = idx + 1; i < s.size(); i++) {
            unordered_set<char> blocked;
            for(int j = 1; j <= 2 && i - j >= 0; j++)
                blocked.insert(s[i-j]);
            for(char c = 'a'; c < s[i-1] + offset; c++) {
                if(blocked.find(c) == blocked.end()) {
                    s[i] = c;
                    break;
                }
            }
        }
        return s;
    }
};

