vector<string> read_file_process_lines(const string& filename) {
    ifstream file(filename);
    vector<string> results;
    string line;

    if(file.is_open()) {
        while(getline(file, line)) {
            results.push_back(line);
        }
        file.close();
    } else {
        cout << "Unable to open file";
    }

    return results;
}

int main() {
    // test the function
    string filename = "test.txt";
    vector<string> lines = read_file_process_lines(filename);
    for(string line : lines) {
        cout << line << "\n";
    }
    return 0;
}

