class Solution {
public:
    int longestValidParentheses(string s) {
        int max_len = 0;
        stack<int> stk;
        stk.push(-1);
        for(int i=0; i<s.length(); i++){
            if(s[i] == '(')
                stk.push(i);
            else{
                stk.pop();
                if(!stk.empty())
                    max_len = max(max_len, i - stk.top());
                else
                    stk.push(i);
            }
        }
        return max_len;
    }
};

int main() {
    Solution s;
    cout<<s.longestValidParentheses("(()");
    return 0;
}

