double mean(const vector<double>& v) {
    double total = 0;
    for (double value : v) {
        total += value;
    }
    return total / v.size();
}

double stdev(const vector<double>& v, double mean_val) {
    double variance = 0;
    for (double value : v) {
        variance += (value - mean_val) * (value - mean_val);
    }
    variance /= v.size();
    return sqrt(variance);
}

double likelihood(const vector<double>& dataset, double lambda, double gamma) {
    double prob = 1;
    for (double data : dataset) {
        prob *= lambda / (gamma + data);
    }
    prob *= pow(gamma, dataset.size());
    return prob;
}

double joint_likelihood(const vector<vector<double>>& datasets, const vector<double>& lambdas, const vector<double>& gammas) {
    double joint_prob = 1;
    for (size_t i = 0; i < datasets.size(); ++i) {
        joint_prob *= likelihood(datasets[i], lambdas[i], gammas[i]);
    }
    return joint_prob;
}

