struct Aresta{
    int origem;
    int destino;
    int peso;
};

class Grafo{
    private:
        int numVertices;
        std::vector<Aresta> arestas;
        int encontrar(int subset[], int i);
        void unir(int subset[], int x, int y);

    public:
        Grafo(int V);
        void addAresta(int u, int v, int w);
        void KruskalMST();

};



