//src/grafos.cpp
#include "grafos.h"
#include <algorithm>
#include <iostream>

Grafo::Grafo(int V){
    numVertices = V;
}

void Grafo::addAresta(int u, int v, int w){
    Aresta aresta;
    aresta.origem = u;
    aresta.destino = v;
    aresta.peso = w;
    arestas.push_back(aresta);
}

int Grafo::encontrar(int subset[], int i){
    if (subset[i] == i)
        return i;
    return encontrar(subset, subset[i]);
}

void Grafo::unir(int subset[], int x, int y){
    int xroot = encontrar(subset, x);
    int yroot = encontrar(subset, y);
    subset[xroot] = yroot;
}

void Grafo::KruskalMST(){
    std::vector<Aresta> resultado;
    std::sort(arestas.begin(), arestas.end(), [](Aresta const &a, Aresta const &b) { return a.peso < b.peso; });

    int *subset = new int[numVertices];

    for (int i = 0; i < numVertices; i++)
        subset[i] = i;

    for (std::size_t i = 0; i < arestas.size(); i++) {
        Aresta next_aresta = arestas[i];
        int x = encontrar(subset, next_aresta.origem);
        int y = encontrar(subset, next_aresta.destino);

        if (x != y) {
            unir(subset, x, y);
            resultado.push_back(next_aresta);
        }
    }

    std::cout << "Arestas no MST so:\n";
    for (std::size_t i = 0; i < resultado.size(); i++) {
        Aresta aresta = resultado[i];
        std::cout << "Origem: " << aresta.origem << " Destino: " << aresta.destino << " Peso: " << aresta.peso << "\n";
    }

    delete[] subset;
}

