The code does the following:

- It counts the occurrences of each lowercase English letter in the string.
- Then, for each '?' character in the string, it finds the lowercase English letter with the minimum count and replaces it.
- The selected letters are stored in a string and sorted to ensure letters are in ascending order.
- The string 's' is then iterated again to replace each '?' character with one of the selected letters in ascending order.
- Finally, the resulting minimized string is returned.
*/

#include <iostream>
#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    string minimizeStringValue(string s) {
        int n = s.size();
        string ans = ""; 
        int A[26] = {0}; 

        for (auto i : s) {
            if (i != '?') {
                A[i - 'a']++; 
            }
        }

        string dum = ""; 

        for (int i = 0; i < n; i++) {
            if (s[i] == '?') {
                int min = 0, cnt = n + 1;
                for (int i = 0; i < 26; i++) {
                    if (A[i] < cnt) {
                        cnt = A[i]; 
                        min = i;    
                    }
                }
                A[min]++; 
                dum += (char)('a' + min); 
            }
        }

        int j = 0;
        sort(dum.begin(), dum.end());

        for (int i = 0; i < n; i++) {
            if (s[i] == '?') {
                s[i] = dum[j]; 
                j++; 
            }
            ans += s[i]; 
        }

        return ans; 
    }
};

int main() {
    Solution obj;
    string s = "a?a?";
    cout << obj.minimizeStringValue(s);
    return 0;
}

