def get_frequent_words(text, k):
    # split the input text into words
    words = text.split()

    # count frequency of each word of length k
    word_freq = {w: sum(1 for _ in words[i:i+k] if w == words[i:i+k]) for i in range(0, len(words))}

    # get maximum frequency
    max_freq = max(word_freq.values())

    # return all words with maximum frequency
    return [word for word in word_freq if word_freq[word] == max_freq]


def get_closest_words(text):
    # split the input text into words
    words = text.split()

    # calculate Levenshtein distances among all pairs of words
    n = len(words)
    distances = [[0 for _ in range(n)] for _ in range(n)]
    for i in range(n):
        for j in range(i+1, n):
            distances[i][j] = distances[j][i] = levenshtein_distance(words[i], words[j])

    # find the closest pair of words
    min_distance = min([min(row) for row in distances])
    for i in range(n):
        for j in range(i+1, n):
            if distances[i][j] == min_distance:
                return words[i], words[j]


def levenshtein_distance(s, t):
    """
    Compute the Levenshtein distance between two strings s and t.
    The function returns the number of deletions, insertions, substitutions required to transform s into t.
    """

    # create distance matrix
    m, n = len(s), len(t)
    d = [[0 for _ in range(n+1)] for _ in range(m+1)]

    # initialize distance matrix
    for i in range(m+1):
        d[i][0] = i
    for j in range(n+1):
        d[0][j] = j

    # fill in the distance matrix
    for j in range(1, n+1):
        for i in range(1, m+1):
            if s[i-1] == t[j-1]:
                substitution_cost = 0
            else:
                substitution_cost = 1

            d[i][j] = min(d[i-1][j] + 1,                      # deletion
                          d[i][j-1] + 1,                      # insertion
                          d[i-1][j-1] + substitution_cost)   # substitution

    return d[m][n]


