struct Trie {
    Trie* next[10];
    bool isEnd = 0;

    Trie() {
        fill(next, next+10, (Trie*)NULL);
    }

    ~Trie() {
        for (int i=0; i<10; ++i) {
            if (next[i] !=NULL) {
                delete next[i];
            }
        }
    }

    void insert(string word) {
        Trie* Node=this;
        for(char c: word){
            int i=c-'0';
            if(Node->next[i]==NULL)
                Node->next[i]=new Trie();
            Node=Node->next[i];
        }
        Node->isEnd=1;
    }

    int commonPrefix(string s) {
        Trie* Node = this;
        int len=0;
        for(char c : s){
            int i = c - '0';
            if(Node->next[i]==NULL) return len;
            Node = Node->next[i];
            len++;
        }
        return len;

    }
};

