std::vector<float> simple_sgd_step(std::vector<float> parameter_values, float learning_rate) {
    // Convert std::vector to torch::Tensor
    torch::Tensor parameters = torch::tensor(parameter_values, torch::requires_grad());

    // Initialize optimizer
    torch::optim::SGD optimizer(std::vector<torch::Tensor>({parameters}), learning_rate);

    // Simulate a simple loss gradient
    parameters.grad() = torch::ones_like(parameters);

    // Perform optimizer step (parameter update)
    optimizer.step();

    // Convert torch::Tensor back to std::vector
    return parameters.detach().toType(torch::kFloat32).toStdVector();
}

