    const int mod = 1e9 + 7;

    long long power(long long a,long long b){
        long long res = 1;
        while(b){
            if(b&1) res = (res * a) % mod;
            a = (a * a) % mod;
            b >>= 1;
        }
        return res;
    }

public:
    int sumOfPower(std::vector<int>& nums, int k){
        int n = nums.size();
        std::vector<std::vector<long long>> dp(n+1,std::vector<long long>(k+1,0));
        dp[0][0] = 1;
        for(int pos = 1 ; pos <= n ; pos++){
            for(int len = pos ; len >= 1 ; len--){
                for(int sum = nums[pos-1] ; sum <= k ; sum++){
                    dp[len][sum] = (dp[len][sum] + dp[len-1][sum-nums[pos-1]] ) % mod;
                }
            }
        }
        long long ans = 0;
        for(int len = 1 ; len <= n ; len++) ans = (ans + dp[len][k] * power(2,n-len)) % mod;
        return ans;
    }
