import java.util.*;

public class Solution {
    public String lastNonEmptyString(String input_string) {
        Map<Character, Integer> char_frequency = new HashMap<>();
        StringBuilder result = new StringBuilder(); 
        Set<Character> unique_chars = new HashSet<>();

        for (char c : input_string.toCharArray()) {
            char_frequency.put(c, char_frequency.getOrDefault(c, 0) + 1);
        }

        List<Map.Entry<Character, Integer>> sorted_frequency = new ArrayList<>(char_frequency.entrySet());
        sorted_frequency.sort(Map.Entry.<Character, Integer>comparingByValue().reversed());

        int max_frequency = sorted_frequency.get(0).getValue();

        for (Map.Entry<Character, Integer> entry : sorted_frequency) {
            if (entry.getValue() == max_frequency) {
                unique_chars.add(entry.getKey());
            }
            if (entry.getValue() < max_frequency) {
                break;
            }
        }

        for (int i = input_string.length() - 1; i >= 0; --i) {
            if (unique_chars.contains(input_string.charAt(i))) {
                result.append(input_string.charAt(i));
                unique_chars.remove(input_string.charAt(i));
            }
            if (unique_chars.isEmpty()) {
                break;
            }
        }

        return result.reverse().toString();
    }
}

