/*
https://leetcode.com/problems/sum-root-to-leaf-numbers/

Given a binary tree, each node contains an integer number. The depth of the tree is smaller than or equal to 10.

Find the sum of all numbers that can be formed by concatenating the numbers from the root to any leaf.

*/

// Definition for a binary tree node.
struct TreeNode {
  int val;
  TreeNode *left;
  TreeNode *right;
  TreeNode(int x) : val(x), left(NULL), right(NULL) {}
};

class Solution {
public:
    int sumNumbers(TreeNode* root) {
        return sumNumbersDFS(root, 0);
    }

private:
    int sumNumbersDFS(TreeNode* node, int sum) {
        if (!node) return 0;
        if (!node->left && !node->right) return sum * 10 + node->val;
        return sumNumbersDFS(node->left, sum * 10 + node->val) + sumNumbersDFS(node->right, sum * 10 + node->val);
    }
};

