    public static int countSubmatrices(List<List<Integer>> grid, int k) {
        int ans = 0;
        for (int i = 0; i < grid.size(); ++i) {
            for (int j = 0; j < grid.get(0).size(); ++j) {
                if (i >= 1) grid.get(i).set(j, grid.get(i).get(j) + grid.get(i - 1).get(j));
                if (j >= 1) grid.get(i).set(j, grid.get(i).get(j) + grid.get(i).get(j - 1));
                if (i >= 1 && j >= 1) grid.get(i).set(j, grid.get(i).get(j) - grid.get(i - 1).get(j - 1));
                if (grid.get(i).get(j) <= k) ans++;
            }
        }
        return ans;
    }
}
