class Solution {
    public int minimumDeletions(String word, int k) {
        int[] cnt = new int[26];

        for(char c : word.toCharArray()) {
            cnt[c - 'a']++;
        }

        Arrays.sort(cnt);
        int ans = Integer.MAX_VALUE;
        int startDel = 0;

        for(int i = 0; i < 26; i++) {
            int curDel = startDel;
            int topLimit = cnt[i] + k;
            for(int j = i + 1; j < 26; j++) {
                if(topLimit < cnt[j]) curDel += cnt[j] - topLimit;
            }
            ans = Math.min(ans, curDel);
            startDel += cnt[i];
        }

        return ans;
    }
}
