class Solution {
    public long maximumScoreAfterOperations(int[][] edges, int[] values) {
        List<Integer>[] g = new ArrayList[values.length];
        Arrays.setAll(g, e -> new ArrayList<>());
        for (int[] e : edges) {
            int x = e[0], y = e[1];
            g[x].add(y);
            g[y].add(x);
        }
        long ans = dfs(0, -1, g, values);
        return ans;
    }

    private long dfs(int x, int fa, List<Integer>[] g, int[] values) {
        long sum = values[x];
        long maxLoss = 0;
        for (int y : g[x]) {
            if (y != fa) {
                long cur = dfs(y, x, g, values);
                sum += cur;
                maxLoss = Math.max(maxLoss, cur);
            }
        }
        if (g[x].size() != 1) { // if not a leaf
            sum -= maxLoss;
        }
        return sum;
    }
}
