class Solution {
    public int minOrAfterOperations(int[] nums, int k) {
        int ans = 0;
        int mask = 0;
        for (int b = 29; b >= 0; b--) {
            mask |= 1 << b;
            int cnt = 0; // Count of operations
            int and = -1; // -1 in binary is all 1s
            for (int x : nums) {
                if ((x & mask) != and) {
                    and = x & mask;
                    cnt++; // Merge x, increment operation count
                }
            }
            if (cnt > k) {
                ans |= 1 << b; // This bit of the answer must be 1
                mask ^= 1 << b; // Ignore this bit in further considerations
            }
        }
        return ans;
    }
}
