class Solution {
    public int minimumKeypadPushes(String word) {
        // Initialize the frequency array to count the occurrence of each letter.
        int[] freq = new int[26];
        for (char c : word.toCharArray()) {
            freq[c - 'a']++;
        }

        // Sort the frequency array in descending order.
        Arrays.sort(freq);
        int index = 25; // Start from the highest frequency.

        int pushes = 0; // Total number of pushes needed.
        int currentKey = 1; // Current key count, starts from 1.
        int currentKeyPushes = 1; // Pushes needed for the current key.

        // Start assigning the most frequent letters to the keys
        // with the least number of pushes needed.
        while (index >= 0 && freq[index] > 0) {
            pushes += freq[index] * currentKeyPushes; // Calculate pushes for the current letter.
            if (currentKey % 3 == 0) {
                // If the current key is fully utilized, move to the next key.
                currentKeyPushes++;
            }
            currentKey++;
            index--;
        }

        return pushes;
    }
}
