class Solution {
    public int numberOfSubmatrices(char[][] grid) {
        int ans = 0;
        int m = grid.length;
        int n = grid[0].length;
        int[][][] sum = new int[m + 1][n + 1][2];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                sum[i + 1][j + 1][0] = (grid[i][j] == 'X' ? 1 : 0) + sum[i][j + 1][0] - sum[i][j][0];
                sum[i + 1][j + 1][1] = (grid[i][j] == 'Y' ? 1 : 0) + sum[i + 1][j][1] + sum[i][j + 1][1] - sum[i][j][1];
                if (grid[i][j] != '.') {
                    sum[i + 1][j + 1][grid[i][j] == 'Y' ? 1 : 0]++;
                }
                for (int x = 0; x <= i; ++x) {
                    for (int y = 0; y <= j; ++y) {
                        int xCount = sum[i + 1][j + 1][0] - sum[x][j + 1][0] - sum[i + 1][y][0] + sum[x][y][0];
                        int yCount = sum[i + 1][j + 1][1] - sum[x][j + 1][1] - sum[i + 1][y][1] + sum[x][y][1];
                        if (xCount == yCount && xCount > 0) {
                            ans++;
                        }
                    }
                }
            }
        }
        return ans;
    }
}
