class Solution {
    private static final int MX = 31622;
    private static final int[] PI = new int[MX + 1];

    static {
        for (int i = 2; i <= MX; i++) {
            if (PI[i] == 0) { // i 是质数
                PI[i] = PI[i - 1] + 1;
                for (int j = i * i; j <= MX; j += i) {
                    PI[j] = -1; // 标记 i 的倍数为合数
                }
            } else {
                PI[i] = PI[i - 1];
            }
        }
    }

    public int nonSpecialCount(int l, int r) {
        return r - l + 1 - (pi(r) - pi(l - 1));
    }

    private int pi(int x) {
        if (x < MX) return PI[x];
        int cnt = 0;
        for (int i = 2; i * i <= x; i++) {
            if (PI[i] != -1) cnt++;
        }
        return cnt;
    }
}
